import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowRight } from 'lucide-react';

const slides = [
  {
    title: 'Impulsa tu negocio',
    subtitle: 'Asesoría experta en consultoría empresarial',
    cta: 'Comenzar Ahora'
  },
  {
    title: 'Transforma tus ideas',
    subtitle: 'Soluciones personalizadas para tu empresa',
    cta: 'Conocer Más'
  },
  {
    title: 'Resultados exitosos',
    subtitle: 'Más de 500 clientes satisfechos',
    cta: 'Contáctanos'
  }
];

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Cambio automático cada 5 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000); // 5000ms = 5 segundos
    return () => clearInterval(interval); // Limpiar al desmontar
  }, []);

  return (
    <section className="pt-20 min-h-screen bg-gradient-to-br from-green-50 via-white to-green-100 flex items-center">
      <div className="container mx-auto px-4 relative">
        <AnimatePresence>
          <motion.div
            key={currentSlide}
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.8 }}
            className="text-center"
          >
            <h1 className="text-5xl lg:text-6xl font-bold mb-4">
              <span className="bg-gradient-to-r from-green-600 to-emerald-700 bg-clip-text text-transparent">
                {slides[currentSlide].title}
              </span>
            </h1>
            <p className="text-xl text-gray-700 mb-8">{slides[currentSlide].subtitle}</p>
            <button
              className="px-8 py-4 bg-gradient-to-r from-green-600 to-emerald-700 text-white font-semibold rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 flex items-center gap-3 justify-center"
            >
              {slides[currentSlide].cta} <ArrowRight className="w-5 h-5" />
            </button>
          </motion.div>
        </AnimatePresence>

        {/* Controles del carrusel */}
        <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 flex gap-3">
          {slides.map((_, index) => (
            <span
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-3 h-3 rounded-full cursor-pointer ${
                index === currentSlide ? 'bg-green-600' : 'bg-green-300'
              }`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Hero;
