import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowRight } from 'lucide-react';

const Hero = () => {
  // Slides del carrusel
  const slides = [
    {
      title: 'Impulsa tu negocio',
      subtitle: 'Asesoría experta en consultoría empresarial',
      cta: 'Comenzar Ahora',
      bgImage: 'PROJECT(1)/node_modules/public/imagenes/slide1.jpg' // ruta de tu imagen de fondo
    },
    {
      title: 'Transforma tus ideas',
      subtitle: 'Soluciones personalizadas para tu empresa',
      cta: 'Conocer Más',
      bgImage: '/imagenes/slide2.jpg'
    },
    {
      title: 'Resultados exitosos',
      subtitle: 'Más de 500 clientes satisfechos',
      cta: 'Contáctanos',
      bgImage: '/imagenes/slide3.jpg'
    }
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  // Cambio automático del carrusel cada 5 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000); // 5000ms = 5s
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <section
      id="inicio"
      className="pt-20 min-h-screen bg-gradient-to-br from-green-50 via-white to-green-100"
    >
      <div className="container mx-auto px-4 py-20">
        <div className="relative">
          <AnimatePresence mode="wait">
            {slides.map(
              (slide, index) =>
                index === currentSlide && (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, x: 50 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -50 }}
                    transition={{ duration: 0.8 }}
                    className="relative w-full h-96 flex items-center justify-center text-center rounded-2xl overflow-hidden"
                    style={{
                      backgroundImage: `url(${slide.bgImage})`,
                      backgroundSize: 'cover',
                      backgroundPosition: 'center'
                    }}
                  >
                    <div className="bg-black/40 p-8 rounded-xl text-white max-w-3xl">
                      <h1 className="text-4xl lg:text-5xl font-bold mb-4">
                        {slide.title}
                      </h1>
                      <p className="text-lg lg:text-xl mb-6">{slide.subtitle}</p>
                      <button className="px-6 py-3 bg-green-600 hover:bg-emerald-700 rounded-2xl font-semibold transition flex items-center gap-2 mx-auto">
                        {slide.cta}
                        <ArrowRight className="w-5 h-5" />
                      </button>
                    </div>
                  </motion.div>
                )
            )}
          </AnimatePresence>

          {/* Puntos de navegación */}
          <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-3">
            {slides.map((_, index) => (
              <button
                key={index}
                className={`w-3 h-3 rounded-full transition ${
                  index === currentSlide
                    ? 'bg-green-600'
                    : 'bg-green-300 hover:bg-green-500'
                }`}
                onClick={() => setCurrentSlide(index)}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
