import React, { useState } from "react";
import ReCAPTCHA from "react-google-recaptcha";

const IngresarDenuncia = () => {
  const [relacion, setRelacion] = useState("");
  const [esAnonima, setEsAnonima] = useState("");
  const [captchaValue, setCaptchaValue] = useState(null);

  const [formData, setFormData] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    confirmarCorreo: "",
    telefono: "",
    fechaOcurrencia: "",
    identifica: "",
    descripcion: "",
    comoConocio: "",
    lugar: "",
    archivo: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const handleCaptchaChange = (value) => {
    setCaptchaValue(value);
    console.log("Captcha value:", value);
  };

const handleSubmit = async (e) => {
  e.preventDefault();

  if (!captchaValue) {
    alert("Por favor, completa el captcha antes de enviar.");
    return;
  }

  const data = {
    ...formData,
    Relacion: relacion,
    OP: esAnonima === "Si" ? "SI" : "NO",
    captchaValue
  };

  try {
    const res = await fetch("http://localhost:3000/ingresar-denuncia", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    });

    const result = await res.json();

    if (result.success) {
      alert("Denuncia enviada con éxito ✅");
      window.location.href = `/respuesta/${result.id}`;
    } else {
      alert(result.msg || "Hubo un error ❌");
    }
  } catch (err) {
    console.error("Error:", err);
    alert("Error de conexión con servidor");
  }
};



  return (
    <main className="flex-1 container mx-auto px-6 py-32 max-w-4xl">
      <h1 className="text-5xl font-bold text-center mb-16 text-green-700">
        Ingresar Denuncia
      </h1>

      <form
        className="bg-white p-10 rounded-2xl shadow-xl space-y-8"
        onSubmit={handleSubmit}
      >
        {/* Relación con Bancrece */}
        <div>
          <label className="block font-medium mb-2">
            ¿Cuál es su relación con Bancrece?
          </label>
          <select
            name="relacion"
            value={relacion}
            onChange={(e) => setRelacion(e.target.value)}
            className="w-full border rounded-lg p-3"
            required
          >
            <option value="">Seleccione...</option>
            <option value="Socio">Socio</option>
            <option value="Colaborador">Colaborador</option>
            <option value="Proveedor">Proveedor</option>
            <option value="Otros">Otros</option>
          </select>
        </div>

        {/* Es anónima */}
        <div>
          <label className="block font-medium mb-2">
            ¿Desea que la denuncia sea anónima?
          </label>
          <select
            name="esAnonima"
            value={esAnonima}
            onChange={(e) => setEsAnonima(e.target.value)}
            className="w-full border rounded-lg p-3"
            required
          >
            <option value="">Seleccione...</option>
            <option value="Si">Sí</option>
            <option value="No">No</option>
          </select>
        </div>

        {/* Campos si NO es anónima */}
        {esAnonima === "No" && (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <input
                type="text"
                name="nombre"
                placeholder="Nombre"
                value={formData.nombre}
                onChange={handleChange}
                className="border rounded-lg p-3 w-full"
                required
              />
              <input
                type="text"
                name="apellido"
                placeholder="Apellido"
                value={formData.apellido}
                onChange={handleChange}
                className="border rounded-lg p-3 w-full"
                required
              />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <input
                type="email"
                name="correo"
                placeholder="Correo"
                value={formData.correo}
                onChange={handleChange}
                className="border rounded-lg p-3 w-full"
                required
              />
              <input
                type="email"
                name="confirmarCorreo"
                placeholder="Confirmar correo"
                value={formData.confirmarCorreo}
                onChange={handleChange}
                className="border rounded-lg p-3 w-full"
                required
              />
            </div>
            <input
              type="tel"
              name="telefono"
              placeholder="Teléfono de contacto"
              value={formData.telefono}
              onChange={handleChange}
              className="border rounded-lg p-3 w-full"
              required
            />
          </>
        )}

        {/* Campos comunes */}
        <input
          type="date"
          name="fechaOcurrencia"
          value={formData.fechaOcurrencia}
          onChange={handleChange}
          className="border rounded-lg p-3 w-full"
          required
        />

        <div>
          <label className="block font-medium mb-2">
            ¿Puede identificar a las personas comprometidas con la infracción?
          </label>
          <select
            name="identifica"
            value={formData.identifica}
            onChange={handleChange}
            className="w-full border rounded-lg p-3"
            required
          >
            <option value="">Seleccione...</option>
            <option value="Si">Sí</option>
            <option value="No">No</option>
            <option value="No sabe/No desea revelar">No sabe/No desea revelar</option>
          </select>
        </div>

        <textarea
          name="descripcion"
          placeholder="Describa los hechos"
          value={formData.descripcion}
          onChange={handleChange}
          className="border rounded-lg p-3 w-full h-40"
          required
        />

        <div>
          <label className="block font-medium mb-2">
            ¿Cómo tomó conocimiento de la situación?
          </label>
          <select
            name="comoConocio"
            value={formData.comoConocio}
            onChange={handleChange}
            className="w-full border rounded-lg p-3"
            required
          >
            <option value="">Seleccione...</option>
            <option value="Lo escuché">Lo escuché</option>
            <option value="Me pasó a mí">Me pasó a mí</option>
            <option value="Lo observé">Lo observé</option>
            <option value="Un compañero de oficina me lo mencionó">Un compañero de oficina me lo mencionó</option>
            <option value="Por casualidad encontré un documento">Por casualidad encontré un documento</option>
            <option value="Otros">Otros</option>
          </select>
        </div>

        <div>
          <label className="block font-medium mb-2">Indique el lugar de ocurrencia</label>
          <select
            name="lugar"
            value={formData.lugar}
            onChange={handleChange}
            className="w-full border rounded-lg p-3"
            required
          >
            <option value="">Seleccione...</option>
            <option value="Casa Matriz">Casa Matriz</option>
            <option value="Moneda">Moneda</option>
            <option value="Viña del Mar">Viña del Mar</option>
            <option value="Talca">Talca</option>
            <option value="Concepción">Concepción</option>
            <option value="Puerto Montt">Puerto Montt</option>
            <option value="Otro">Otro</option>
          </select>
        </div>

        <div>
          <label className="block font-medium mb-2">Adjuntar archivo</label>
          <input
            type="file"
            name="archivo"
            onChange={handleChange}
            className="w-full"
          />
        </div>

        {/* reCAPTCHA */}
        <div className="flex justify-center mt-4">
          <ReCAPTCHA
            sitekey="6LfexsMrAAAAAGNY3HbDtlmIgFNztMj9iXyjjmOm"
            onChange={handleCaptchaChange}
          />
        </div>

        <button
          type="submit"
          className="w-full bg-green-600 text-white py-4 rounded-2xl font-semibold hover:bg-green-700 transition text-lg mt-4"
        >
          Enviar
        </button>
      </form>
    </main>
  );
};

export default IngresarDenuncia;
